function(){
  	var params = {{datalayer.criteoParams}};
	var criteo_id = {{Criteo - Partner ID}};
	var criteo_site_type = {{Criteo - Site Type}};
	var page = params.PageType;

	var return_params = [
		{ event: "setAccount", account: criteo_id },
        { event: "setEmail", email: params.email },
 		{ event: "setSiteType", type: criteo_site_type }
    ];

	if(page == 'HomePage')
    {
		return_params.push({event: page});
    }
	else if(page == 'ListingPage')
    {
      return_params.push({event: page, item: params.ProductIDList});
    }
	else if(page == 'ProductPage')
    {
      return_params.push({event: page, item: params.ProductID});
    }
	else if(page == 'BasketPage')
    {
      return_params.push({event: page, item: params.ProductBasketProducts});
    }
	else if(page == 'TransactionPage')
    {
      return_params.push({event: page, id: params.TransactionID, item: params.ProductTransactionProducts});
    }

	return return_params;
}

