function(){
    var productDetails = {{datalayer.productDetails}};

    if(!productDetails)
        return false;

    var proInfo = productDetails.product;
    var proRevi = proInfo.reviews;

    var obj = {
        '@context': 'http://schema.org',
        '@type': 'Product',
        'description': proInfo.meta_description ? proInfo.meta_description : '',
        'name': proInfo.name ? proInfo.name : '',
        'image': proInfo.image_url ? proInfo.image_url : '',
        'itemCondition' : 'http://schema.org/NewCondition',
        'offers': {
            "@type": "Offer",
            "availability": "http://schema.org/InStock",
            "price": proInfo.price_final,
            "priceCurrency": {{datalayer.current_currency}}
        }
    };


    if(proRevi > 0)
    {
      	obj.aggregateRating = {
            "@type": "AggregateRating",
            "ratingValue": proInfo.rating.toString(),
            "bestRating": "5",
            "worstRating": "1",
            "reviewCount": proInfo.reviews.toString()
        };

      	var productUserReviews = productDetails.reviews;

        var reviews_array = [];
        for (var key in productUserReviews) {
            var re = productUserReviews[key];
            var temp = {
                "@type": "Review",
                "author": re.author,
                "datePublished": re.date_added,
                "description": re.text,
                "reviewRating": {
                    "@type": "Rating",
                    "bestRating": "5",
                    "ratingValue": re.rating,
                    "worstRating": "0"
                }
            };
            reviews_array.push(temp);
        }
        obj.review = reviews_array;
    }
    return obj;
}

function(){
  	var params = {{datalayer.googleDynamicRemarketing}};
	var prefix = {{Google Dynamic Remarketing - Product id prefix}} != 'EMPTY' ? {{Google Dynamic Remarketing - Product id prefix}} : '';
	var sufix = {{Google Dynamic Remarketing - Product id sufix}} != 'EMPTY' ? {{Google Dynamic Remarketing - Product id sufix}} : '';
	var dynx_enabled = {{Google Dynamic Remarketing - Dynx compatibility}} == 'TRUE';
	var page_type = params.ecomm_pagetype;
	var product_info = page_type != 'home' && page_type != 'other';
	//Add prefix and sufix to product id
		if(product_info)
		{
			var product_id = params.ecomm_prodid;
			if( typeof product_id === 'string' ) {
				if(product_id.indexOf(prefix) < 0)
		        	product_id = prefix+product_id;
				if(product_id.indexOf(sufix) < 0)
		        	product_id = product_id+sufix;
		    }
		    else
		    {
		    	var prodid_array = [];

		    	for (var key in product_id) {

		    		poid = product_id[key];

		    		if(poid.indexOf(prefix) < 0)
			        	poid = prefix+poid;
					if(poid.indexOf(sufix) < 0)
			        	poid = poid+sufix;

			        prodid_array.push(poid);
			    }

			    product_id = prodid_array;
		    }

			params.ecomm_prodid = product_id;
		}

		if(dynx_enabled)
        {
          	var dynx_params = {};

        	if(product_info)
        	{
	          	dynx_params.dynx_itemid = product_id;
	            dynx_params.dynx_itemid2 = params.dynx_itemid2;
              	if(params.ecomm_totalvalue)
            		dynx_params.dynx_totalvalue = params.ecomm_totalvalue;
        	}

            if(page_type == 'product' || page_type == 'category')
            	page_type = 'offerdetail';
            else if(page_type == 'cart')
            	page_type = 'conversionintent';
           	else if(page_type == 'purchase')
            	page_type = 'conversion';

            dynx_params.dynx_pagetype = page_type;

          	params = dynx_params;
        }
        else
        {
        	delete params.dynx_itemid;
        	delete params.dynx_itemid2;
        }
	//END

	return params;
}

