function (){
    function get_id(product_id) {
      var prefix = {{GDR - Product id prefix}} != 'EMPTY' ? {{GDR - Product id prefix}} : '';
      var sufix = {{GDR - Product id sufix}} != 'EMPTY' ? {{GDR - Product id sufix}} : '';

      if(product_id.indexOf(prefix) < 0)
        product_id = prefix+product_id;
      if(product_id.indexOf(sufix) < 0)
        product_id = product_id+sufix;

      return product_id;
  }
  function get_product_ids(productsListed) {
    var product_ids = [];
    for (var key in productsListed) {
      var prod_info = typeof productsListed[key].product != 'undefined' ? productsListed[key].product : productsListed[key];
      product_ids.push(get_id(prod_info.product_id_gdr_1));
    }
    return product_ids;
  }

    var dynx_enabled = {{GDR - Dynx compatibility}} == 'TRUE';
    var evet_name = {{GDR - Event name}};    
    var dataLayerData = {{datalayer.data}};
    var current_view = {{datalayer.current_view}};
    var object = {};

    if(current_view == 'homepage') {
       object = {
        'ecomm_pagetype' : 'home'
      };
    } else if(current_view == 'product') {
      var product = {{datalayer.product_details}}.product;
      var object = {
        'ecomm_prodid' : get_id(product.product_id_gdr_1),
        'ecomm_totalvalue' : product.prices.price.price,
        'ecomm_pagetype' : 'product',
        'ecomm_category' : product.category,
        'items' : [{
          'id' : get_id(product.product_id_gdr_1),
          'google_business_vertical' : 'retail'
          }],
        'value' : product.prices.price.price,
      };

      if(dynx_enabled) {
        object['dynx_itemid'] = get_id(product.product_id_gdr_1);
        object['dynx_itemid2'] = get_id(product.product_id_gdr_2);
        object['dynx_totalvalue'] = product.prices.price.price;
        if(evet_name == 'product' || evet_name == 'category')
          object['dynx_pagetype'] = 'offerdetail';
        else if(evet_name == 'add_to_cart')
          object['dynx_pagetype'] = 'conversionintent';
        else if(evet_name == 'purchase')
          object['dynx_pagetype'] = 'conversion';
      }
    } else if(current_view == 'cart' || current_view == 'checkout') {
      var product_ids = get_product_ids({{datalayer.cart_products}});
      var cart_total = {{datalayer.cart_products.total}};

      var items = [];
      product_ids.forEach( function(value, index, array_element) {
        items.push({'id' : value, 'google_business_vertical' : 'retail'});
      });

      pagetype = 'cart';

      object = {
        'ecomm_prodid' : product_ids, 
        'ecomm_pagetype' : pagetype,
        'ecomm_totalvalue' : cart_total,
        'items' : items,
      };
    } else if(current_view == 'category' || current_view == 'search') {
      var product_ids = get_product_ids({{datalayer.products_listed}});
      var total_listed = {{datalayer.products_listed.total}};

      var items = [];
      product_ids.forEach( function(value, index, array_element) {
        items.push({'id' : value, 'google_business_vertical' : 'retail'});
      });

      var pagetype = current_view == 'category' ? 'category' : '';
      pagetype = pagetype == '' && current_view == 'search' ? 'searchresults' : pagetype;

      object = {
        'ecomm_prodid' : product_ids, 
        'ecomm_pagetype' : pagetype,
        'ecomm_totalvalue' : total_listed,
        'items' : items,
      };

      if(current_view == 'category')
        object['ecomm_pcat'] = {{datalayer.current_categories}}.join(',');  
    } else if(current_view == 'purchase') {
      var order_data = {{datalayer.order_data}};
      var product_ids = get_product_ids(order_data.products);
      var total_listed = order_data.order.total;
      var pagetype = 'purchase';

      var items = [];
      product_ids.forEach( function(value, index, array_element) {
        items.push({'id' : value, 'google_business_vertical' : 'retail'});
      });

      object = {
        'ecomm_prodid' : product_ids, 
        'ecomm_pagetype' : pagetype,
        'ecomm_totalvalue' : total_listed,
        'items' : items,
        'value' : total_listed
      };

    } else {
        object = {
         'ecomm_pagetype' : 'other'
        };
    }

    return object; 
}

