function(){
    var productDetails = {{datalayer.product_details}};

    if(!productDetails)
        return false;

    var proInfo = productDetails.product;
    var proRevi = productDetails.reviews;
    var brand = {
        "@type": "Brand",
        "name": proInfo.manufacturer ? proInfo.manufacturer : ''
    };
    var obj = {
        '@context': 'http://schema.org',
        '@type': 'Product',
        'description': proInfo.sort_description ? proInfo.sort_description : '',
        'name': proInfo.name ? proInfo.name : '',
        'image': proInfo.image_url ? proInfo.image_url : '',
        'itemCondition' : 'http://schema.org/NewCondition',
      	'brand': brand,
      	'sku': proInfo.sku ? proInfo.sku : '',
      	'mpn': proInfo.mpn ? proInfo.mpn : '',
        'offers': {
            "@type": "Offer",
            "availability": proInfo.availability,
            "price": proInfo.prices.price.price,
            "priceCurrency": {{datalayer.current_currency}},
      		"priceValidUntil": proInfo.priceValidUntil,
      		"url": proInfo.url ? proInfo.url : '',
      		"itemOffered" : {
            	"@type" : "Thing",
              	"name" : {{datalayer.store_name}}
            }
        }
    };

	if(typeof proInfo.rich_snippets_identifier != 'undefined') {
      var identifier_name = proInfo.rich_snippets_identifier.name;
      var identifier_value = proInfo.rich_snippets_identifier.value;

      if(identifier_name != '' && identifier_value != '')
        obj[identifier_name] = identifier_value;
    }

    if(proRevi.length > 0)
    {
      	obj.aggregateRating = {
            "@type": "AggregateRating",
            "ratingValue": proInfo.ratingValue,
            "bestRating": "5",
            "worstRating": "1",
            "reviewCount": proInfo.reviewCount
        };

      	var productUserReviews = productDetails.reviews;

        var reviews_array = [];
        for (var key in productUserReviews) {
            var re = productUserReviews[key];
            var author = {
              "@type": "Person",
              "name": re.author
            };

            var temp = {
                "@type": "Review",
                "author": author,
                "datePublished": re.date_added,
                "description": re.text,
                "reviewRating": {
                    "@type": "Rating",
                    "bestRating": "5",
                    "ratingValue": re.rating,
                    "worstRating": "0"
                }
            };
            reviews_array.push(temp);
        }
        obj.review = reviews_array;
    }
    return obj;
}

