function(){
  return function(event, email, properties){
    var functionUrl = {{mailchimp.eventUrl}};
    var url = functionUrl(email);
    var now = new Date().toISOString();

    var xhr = new XMLHttpRequest();
    xhr.open('POST', url, true);
    xhr.setRequestHeader('Authorization', 'Bearer MAILCAPIKEY');
    xhr.setRequestHeader('Content-Type', 'application/json');
    var eventData = {
      name: event,
      properties: properties,
      is_syncing: false,
      occurred_at: now
    };
    var data = JSON.stringify(eventData);
    xhr.onreadystatechange = function() {
        if (xhr.readyState === 4) {
          if (xhr.status !== 200){
            // Se produjo un error en la solicitud
            console.error('Mailchimp call error');
            console.error(xhr.status, xhr.statusText);
          }
        }
    };
    xhr.send(data);
  }
}

function(){
  var includeShippingCosts = (new Boolean({{Include shipping costs in total}})).valueOf();
  var orderTotal = parseFloat({{datalayer.order_data.order.total}});
  var shippingCosts = parseFloat({{datalayer.order_data.order.shipping}});

  if(!includeShippingCosts){
    orderTotal -= shippingCosts;
  }

  return orderTotal;

}

