/******************************************************
 * Imports
 ******************************************************/

const callInWindow = require('callInWindow');
const copyFromWindow = require('copyFromWindow');
const injectScript = require('injectScript');
const JSON = require('JSON');
const log = require('logToConsole');
const Object = require('Object');
const makeTableMap = require('makeTableMap');
const setInWindow = require('setInWindow');

/******************************************************
 * Main
 ******************************************************/
log("data: " + JSON.stringify(data));

/**
 * Create a stub API object which queues all calls. When uwt.js loads later
 * it will process this queue and replace the stub with the actual API.
 *
 * "createArgumentsQueue" is not the prefect substitute of our code because:
 * - our uwt.js only overwrites twq.exe but not twq itself
 * - change the existing pattern is impossible.
 */
const twq = function () {
  if (copyFromWindow('twq.exe')) {
    callInWindow('twq.exe.apply', null, arguments);
  } else {
    callInWindow('twq.queue.push', arguments);
  }
};

twq.integration = 'gtm';
twq.queue = [];
// Function signiture: setInWindow(key, value, overrideExisting)
setInWindow('twq', twq, false);

const params = {};
copyParams(data, params, [
  'value',
  'currency',
  'conversion_id',
  'description',
  'search_string',
  'twclid',
  'email_address',
  'phone_number',
  'external_id',
]);
copyContentParams(data, params);
setPageLocation(data, params);
copyAdditionalParams(data, params);

callInWindow('twq', 'event', data.event_id, params);

// Inject a script tag to load uwt.js.
const url = "https://static.ads-twitter.com/uwt.js";
injectScript(url, data.gtmOnSuccess, data.gtmOnFailure, url);

return twq;
/******************************************************
 * Helper Methods
 ******************************************************/

/**
 * Copy a selective list of data properties into params
 */
function copyParams (data, params, keys) {
  keys.forEach(function(key) {
    if (data[key]) {
      params[key] = data[key];
    }
  });
}

/**
 * We allow user to provide multiple content value with the same key.
 * E.g. content_type=coffee,content_type=tea.
 * This will become
 * {
 *.  ...
 *   "contents": [
 *.    { "key":"content_type", "value":"Coffee" },
 *     { "key":"content_value", "value":1 },
 *     { "key":"num_items", "value":3 },
 *     ...
 *     { "key":"content_type", "value":"Tea" },
 *   ]
 *.  ...
 * }
 * in data object.
 *
 * But twq("event") cmd expects
 * {
 *.  ...
 *   "contents": [
 *.    {
 *       "content_type": "Coffee",
 *       "content_value": 1,
 *       "num_items": 3,
 *     },
 *     {
 *       "content_type": "Tea",
 *       ...
 *.    },
 *   ]
 *.  ...
 * }
 * This function put each content into the right content object.
 * The grouping logic is that if a key is appeared in the last content object,
 * we create a new object and put all the non-duplicate keys into the new object,
 * until a duplicate key apprear. In other words, we use the duplicate key as the
 * delimiters.
 */
function copyContentParams (data, params) {  
  if (!data.contents) {
    return;
  }
  const regroupedContents = [{}];
  data.contents.forEach(function (content) {
    const lastObj = regroupedContents[regroupedContents.length - 1];
    if (lastObj.hasOwnProperty(content.key)) {
      const newObj = {};
      newObj[content.key] = content.value;
      regroupedContents.push(newObj);
    } else {
      lastObj[content.key] = content.value;
    }
  });
  params.contents = regroupedContents;
}

/**
 * Populate hide_page_location or page_location based on the value of
 * data.page_location_op and data.page_location
 *
 * The value ofdata.page_location_op could be:
 * 0: Do nothing.
 * 1: Hide Page Location. So we set hide_page_location to true.
 * 2: Overwrite Page Location. So we set page_location to the given value.
 */
function setPageLocation (data, params) {
  switch (data.page_location_op) {
    case 1:
      params.hide_page_location = true;
      break;
    case 2:
      params.page_location = data.page_location;
      break;
    default:
  }
}

/**
 * Copy data.additionalParams into params
 */
function copyAdditionalParams (data, params) {
  if (!data.additionalParams) {
    return;
  }

  const additionalParams = makeTableMap(data.additionalParams, 'name', 'value');
  Object.keys(additionalParams).forEach(function(key) {
    params[key] = additionalParams[key];
  }); 
}


